mapper.select:
SELECT USER_ID 
		,USER_PASSWORD
FROM SHOPPING_USER
WHERE USER_ID = #{userId}
AND USER_PASSWORD = #{userPassword}

mapper.insert:
INSERT INTO SHOPPING_USER
(
	USER_CODE
	, USER_ID
	, USER_PASSWORD
	, USER_PHONE
	, USER_EMAIL
	, USER_ADDRESS
	, USER_ADDRESS_INFO
	, USER_ADMIN
	, USER_BIRTH
	, USER_JOINDAY
	, USER_NAME
) VALUES (
	(SELECT NVL(MAX(USER_CODE) + 1, 1) FROM SHOPPING_USER)
	, #{userId}
	, #{userPassword}
	, #{userPhone}
	, #{userEmail}
	, #{userAddress}
	, #{userAddress_info}
	, 'n'
	, #{userBirth}
	, SYSDATE
	, #{userName}
)

mapper.select:
SELECT USER_ID 
FROM SHOPPING_USER
WHERE USER_ID = #{userId}
